import React, { useState, useEffect } from 'react';
import {
    Grid, TextField, MenuItem, Button, Typography, Container, Paper
} from '@mui/material';

const ShiftForm = ({ onAddShift, currentShift, isEditing, onUpdateShift }) => {
    const [shift, setShift] = useState({
        location: '',
        date: '',
        startTime: '',
        endTime: '',
        mapStaff: 'No',
        gender: 'N/a',
        originalMessage: '',
        coordinator: '',
        assignedTo: '',
        status: ''
    });

    useEffect(() => {
        if (isEditing && currentShift) {
            setShift(currentShift);
        }
    }, [isEditing, currentShift]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setShift({ ...shift, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isEditing) {
            onUpdateShift(shift);
        } else {
            const newShift = {
                ...shift,
                dateReceived: new Date().toLocaleDateString(),
                timeReceived: new Date().toLocaleTimeString()
            };
            onAddShift(newShift);
        }
        setShift({
            location: '',
            date: '',
            startTime: '',
            endTime: '',
            mapStaff: 'No',
            gender: 'N/a',
            originalMessage: '',
            coordinator: '',
            assignedTo: '',
            status: ''
        });
    };

    return (
        <Container component={Paper} elevation={3} style={{ padding: '16px', marginBottom: '20px' }}>
            <Typography variant="h6" gutterBottom>
                {isEditing ? "Edit Shift" : "Add Shift"}
            </Typography>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    {/* Form fields remain the same as before */}
                    {/* ... */}

                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            {isEditing ? "Update Shift" : "Add Shift"}
                        </Button>
                    </Grid>
                </Grid>
            </form>
        </Container>
    );
};

export default ShiftForm;
