import React, { useState } from 'react';
import ShiftForm from './components/ShiftForm';
import ShiftTable from './components/ShiftTable';
import { Container, Typography } from '@mui/material';

const App = () => {
    const [shifts, setShifts] = useState([]);
    const [currentShift, setCurrentShift] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [editIndex, setEditIndex] = useState(null);

    // Handle adding a new shift
    const handleAddShift = (newShift) => {
        setShifts([...shifts, newShift]);
        resetForm();
    };

    // Set the form to editing mode with the selected shift
    const handleEditShift = (index) => {
        setCurrentShift(shifts[index]);
        setIsEditing(true);
        setEditIndex(index);
    };

    // Update the shift at the specific index
    const handleUpdateShift = (updatedShift) => {
        const updatedShifts = shifts.map((shift, index) =>
            index === editIndex ? updatedShift : shift
        );
        setShifts(updatedShifts);
        resetForm(); // Reset the form after updating
    };

    // Delete the shift at the specified index
    const handleDeleteShift = (index) => {
        const updatedShifts = shifts.filter((_, i) => i !== index);
        setShifts(updatedShifts);
    };

    // Reset the form to add mode
    const resetForm = () => {
        setIsEditing(false);
        setCurrentShift(null);
        setEditIndex(null);
    };

    return (
        <Container>
            <Typography variant="h4" align="center" gutterBottom>
                Shift Management
            </Typography>
            <ShiftForm
                onAddShift={handleAddShift}
                currentShift={currentShift}
                isEditing={isEditing}
                onUpdateShift={handleUpdateShift}
            />
            <ShiftTable
                shifts={shifts}
                onEdit={handleEditShift}
                onDelete={handleDeleteShift}
            />
        </Container>
    );
};

export default App;
